func init() {
	en := control.Register("pokemondict", &ctrl.Options[*zero.Ctx]{
		DisableOnDefault: false,
		Brief:            "宝可梦简单图鉴",
		Help: "- 宝可梦图鉴 [xxx]\n"  +
			"[xxx]为搜索内容",
	})

	en.OnRegex(`^宝可梦图鉴\s?(.*)`).SetBlock(true).Handle(func(ctx *zero.Ctx) {
		query := ctx.State["regex_matched"].([]string)[1]
		if query == "" {
			ctx.SendChain(message.Text("你是想查询【百变怪】吗？"))
			return
		}

		matches := fuzzyMatch(query, PokemonDict)
		if len(matches) == 0 {
			ctx.SendChain(message.Text("没有找到相关的宝可梦"))
			return
		}
		
		if len(matches) == 1 {
			pokemonName := matches[0]
			pokemonInfo := PokemonDict[pokemonName]
			ctx.SendChain(message.Text("找到宝可梦：\n", pokemonInfo))
			return
		}

		var listName []string
		for i, v := range matches {
			listName = append(listName, strconv.Itoa(i+1)+"."+v)
		}
		ctx.SendChain(message.Text("找到以下相关宝可梦：\n",
			strings.Join(listName, "\n"),
			"\n输入对应数字获取宝可梦信息，或回复“取消”以结束"))
		
		recv, cancel := zero.NewFutureEvent("message", 999, false, zero.RegexRule(`(取消)|\d+`), zero.OnlyGroup, zero.CheckUser(ctx.Event.UserID)).Repeat()
		after := time.NewTimer(20 * time.Second)
		for {
			select {
			case <-after.C:
				cancel()
				ctx.Send(message.ReplyWithMessage(ctx.Event.MessageID,
					message.Text("等待超时, 搜索结束"),
				))
				return
			case e := <-recv:
				nextcmd := e.Event.Message.String()
				if nextcmd == "取消" {
					cancel()
					after.Stop()
					ctx.Send(message.ReplyWithMessage(ctx.Event.MessageID,
						message.Text("用户取消, 搜索结束"),
					))
					return
				}

				pokemonNum, err := strconv.Atoi(nextcmd)
				if err != nil || pokemonNum < 1 || pokemonNum > len(matches) {
					after.Reset(20 * time.Second)
					ctx.SendChain(message.At(ctx.Event.UserID), message.Text("请输入正确的序号"))
					continue
				}

				pokemonName := matches[pokemonNum-1]
				pokemonInfo := PokemonDict[pokemonName]
				cancel()
				after.Stop()
				ctx.SendChain(message.Text("宝可梦：\n", pokemonInfo))
				return
			}
		}
	})
}

func fuzzyMatch(query string, dict map[string]string) []string {
	var matches []string
	for key := range dict {
		if strings.Contains(key, query) {
			matches = append(matches, key)
		}
	}
	return matches
}
